'use strict';

const api = require('../constants/api.cjs');
require('lodash');

async function fetchServerStatus(ip, bedrock = false) {
  const url = `${api.RouteBases.mcsrvstat}/${bedrock ? "bedrock/" : ""}3/${ip}`;
  const response = await fetch(url);
  return await response.json();
}
async function isServerOnline(ip, bedrock = false) {
  const url = `${api.RouteBases.mcsrvstat}/${bedrock ? "bedrock/" : ""}simple/${ip}`;
  const response = await fetch(url);
  return response.ok;
}

exports.fetchServerStatus = fetchServerStatus;
exports.isServerOnline = isServerOnline;
