import { RouteBases } from '../constants/api.mjs';
import 'lodash';

async function fetchServerStatus(ip, bedrock = false) {
  const url = `${RouteBases.mcsrvstat}/${bedrock ? "bedrock/" : ""}3/${ip}`;
  const response = await fetch(url);
  return await response.json();
}
async function isServerOnline(ip, bedrock = false) {
  const url = `${RouteBases.mcsrvstat}/${bedrock ? "bedrock/" : ""}simple/${ip}`;
  const response = await fetch(url);
  return response.ok;
}

export { fetchServerStatus, isServerOnline };
